(27, 'Sales Engineer', '<table width="100%" cellspacing="0" cellpadding="10">\r\n<tbody>\r\n<tr>\r\n<td class="comURL">\r\n<p><span style="color: #414042; font-family: Arial; font-size: 16px;"><strong><a style="font-size: 14pt; font-style: normal; font-weight: bold; text-decoration: none;">PANSAR Company Sdn Bhd</a></strong></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width="550" align="center" cellspacing="0" cellpadding="0" border="0">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table width="100%" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p><span style="color: #414042; font-family: Arial; font-size: 12px;"> </span></p>\r\n<span style="font-family: Arial; font-size: 12px;">\r\n<div><span style="color: #414042;"><strong>Working in Pansar Company Sdn. Bhd.</strong><br /></span>\r\n<p><span style="color: #414042;"> PANSAR is an established company involved in a broad spectrum of business activities throughout Malaysia, Singapore and Brunei. The Company seeks to recruit people who possess the drive and integrity with a positive attitude and the right qualification to fill the following position:</span></p>\r\n</div>\r\n</span>\r\n<p>&nbsp;</p>\r\n<p><span style="color: #414042; font-family: Arial; font-size: 12px;"> </span></p>\r\n<p><span style="color: #414042; font-family: Arial; font-size: 16px;"><strong>Sales Engineer/ Executive/ Representative</strong></span><span style="color: #414042; font-family: Arial; font-size: 12px;"><br /> Muar (Johor), Kuala Lumpur, Penang, Ipoh (Perak), Kota Kinabalu (Sabah), Lahad Datu (Sabah), Sandakan (Sabah), Tawau (Sabah) </span></p>\r\n<p><span style="color: #414042; font-family: Arial; font-size: 12px;"> </span></p>\r\n<p><span style="color: #414042; font-family: Arial; font-size: 12px;"> </span></p>\r\n<span style="color: #414042; font-family: Arial; font-size: 12px;">\r\n<p><span><strong>Responsibilities: </strong></span></p>\r\n<ul>\r\n<li> Generate new sales leads and follow through sales enquiries to ensure successful closing and continuous customer satisfaction.</li>\r\n<li> Prepare sales related reports and document for the purpose of the sales activities (e.g. quotations, proposals etc.).</li>\r\n<li> Required to enhance the relationship and sales with our existing customers as well as meet the different needs of our customers with the best value-added solutions.</li>\r\n<li> Other duties and responsibilities as assigned from time to time by the Management.</li>\r\n</ul>\r\n</span>\r\n<p>&nbsp;</p>\r\n<p><span style="color: #414042; font-family: Arial; font-size: 12px;"> </span></p>\r\n<span style="color: #414042; font-family: Arial; font-size: 12px;">\r\n<p><strong><span>Requirements: </span></strong></p>\r\n<ul>\r\n<li> Possess a Bachelor Degree/ Advanced Diploma in Mechanical Engineering, Electrical Engineering, Civil Engineering/ Construction or any other fields. Fresh graduates are encouraged to apply. </li>\r\n<li> Preferably with 2 - 3 years'' sales experience.</li>\r\n<li> Able to implement sales strategies and activities and expand customer base and market share.</li>\r\n<li> Good organisational and time management skills and able to multitask; highly self-motivated and possess initiative and integrity.</li>\r\n<li> Result-oriented and a team player, pleasant personality, cooperative, with excellent communication and interpersonal skills.</li>\r\n<li> Good command of written and spoken English, Mandarin and Bahasa Malaysia.</li>\r\n<li> Possess own transport and a valid driving licence.</li>\r\n<li> Kindly indicate the location you wish to apply.</li>\r\n</ul>\r\n</span>\r\n<p>&nbsp;</p>\r\n<span><span style="color: #414042; font-family: Arial; font-size: 12px;">\r\n<div><strong>Applications</strong><br /><br /> You are required to submit your application to:<br /><br /><strong>The Personnel Manager</strong></div>\r\n<div><strong>Pansar Company Sdn Bhd<br /> Wisma Pansar,<br /> 23 - 27 Jalan Bengkel,<br /> P. O. Box 319,<br /> 96000 Sibu.<br /> Sarawak, Malaysia</strong><br /><br /> Or by email to <strong><a href="mailto:hr.admin@pansar.com.my">hr.admin@pansar.com.my</a></strong><br /> Or apply on-line at <strong><a href="http://www.pansar.com.my/">www.pansar.com.my</a> </strong><br /><br /> not later than <strong>31 July , 2012</strong></div>\r\n</span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 100, 4, '2400', 1, 2, 1, '2012-07-10 07:01:54', '2012-07-11 05:40:44', NULL, '2012-08-10 13:40:44', 30, 4),
(28, 'Marketing Executive', '<div>Main Task:</div>\r\n<div></div>\r\n<div>\r\n<ul>\r\n<li>Liaison with potential customers/students, schools, teachers/principals and parents and interested individual.</li>\r\n</ul>\r\n<ul>\r\n<li>Manage and coordinate all marketing, advertising and promotional, exhibitions, educations fair activities.</li>\r\n</ul>\r\n<ul>\r\n<li>Conduct market research to determine market requrements for existing and future products.</li>\r\n</ul>\r\n<ul>\r\n<li>Develope and implement marketing plans and projects for new and existing products/courses.</li>\r\n</ul>\r\n<ul>\r\n<li>Monitor , review and report on marketing activities and results.</li>\r\n</ul>\r\n</div>', 101, 4, '2000', 1, 2, 1, '2012-07-11 03:24:00', '2012-07-11 05:40:44', NULL, '2012-08-10 13:40:44', 19, 5),
(29, 'RELATIONSHIP MANAGER', '<p>Roles and Resposibilities:</p>\r\n<ul>\r\n<li>Responsible for the acquisition of new-to-bank (NTB) Affluent Banking customer</li>\r\n<li>Prospect, identify and cross sell the Affluent Banking Value Proposition to target segment</li>\r\n<li>Present value proposition to target audience through colaboration with internal business partners.</li>\r\n<li>Work in partnership with assigned Affluent Banking Centre Team to enhance vicinity customer base.</li>\r\n<li>Managed working relationship with campaign team and products partners.</li>\r\n<li>Fixing of appointments through networking.</li>\r\n<li>Conduct are required KYC, AML Procedure and documentation.</li>\r\n<li>Reporting to Branch Manager</li>\r\n</ul>', 103, 2, '-', 1, 2, 0, '2012-07-11 08:34:03', '2012-07-11 09:05:18', NULL, '2012-08-10 17:05:18', 9, 5),
(30, 'CUSTOMER RELATIONSHIP MANAGER', '<p>Roles And Responsibilities:</p>\r\n<ul>\r\n<li>Deposit''s Account Opening and assist Relationship Manager to cross-sell Affluent Banking products and services.</li>\r\n<li>Provide excellent customer service to Affluent Customers.</li>\r\n<li>Conduct all required KYC, AML procedures and documentation.</li>\r\n<li>Reporting to Branch Manager</li>\r\n</ul>\r\n<p>Requirements:</p>\r\n<ul>\r\n<li>You must have a positive attitude with a high degree of initiative and drive.</li>\r\n<li>You should have excellent communication and inter-personal skill, and must be a good team player with a passion towards providing quality customer service.</li>\r\n<li>A strong service mind set and abilityb to take ownership to deliver excellent service at all time is essential.</li>\r\n<li>You should preferably have a degree or a diploma with work experience in the service industries.</li>\r\n<li>Fresh Graduate with a passion for customer service are also welcome to apply.</li>\r\n</ul>', 103, 2, '-', 1, 2, 0, '2012-07-11 08:51:00', '2012-07-11 09:05:37', NULL, '2012-08-10 17:05:37', 9, 4),
