(54, 'TECHNICIAL / ELECTRICAL', '<p><strong>Responsibilities: </strong></p>\r\n<ul>\r\n<li>To carry out      machine research and development, troubleshooting and repairs to maintain improvement.</li>\r\n<li>Maintain &amp; troubleshoot      equipment-related issues in a manner that meets company standards and      ensures minimum machine downtime.</li>\r\n<li>Responsible for new equipment      installation and&nbsp;continuous innovation in process engineering.</li>\r\n<li>Perform scheduled preventive      maintenance (PM) and calibration on a timely basis.</li>\r\n<li>Assist in implementing      equipment improvement projects.</li>\r\n<li>Maintain good housekeeping in      the production area, ensuring good safety practice and also safe custody      of tools, equipment and other properties belonging to company.</li>\r\n</ul>\r\n<p><strong>&nbsp;Requirements:</strong></p>\r\n<ul>\r\n<li>Possess&nbsp;a      Certificate, Diploma or Diploma in Engineering (Electrical/Electronic      /Industrial/Mechanical/Mechatronic )</li>\r\n<li>At least 2 year(s) of working      experience in the related field.</li>\r\n<li>Applicants must be computer      literate.</li>\r\n<li>Applicants must be willing to      work on rotating shifts.</li>\r\n<li>Fresh graduates / Entry-level      applicants may also apply.</li>\r\n</ul>', 115, 5, '1400', 2, 2, 0, '2012-08-08 06:20:25', '2012-08-08 07:18:32', NULL, '2012-09-07 15:18:32', 37, 4),
(55, 'PROCESS TECHNICIAN', '<p><strong>Responsibilities: </strong></p>\r\n<ul>\r\n<li>Taking      ownership of the machine and process, ensure production stability</li>\r\n<li>Responsible for incoming,      in-process and outgoing quality check</li>\r\n<li>Minor trouble-shoot and      feedback to maintenance for major maintenance issues</li>\r\n<li>Participate in team activities      (Self-directed Work team)</li>\r\n<li>Demonstrate an excellent level      of personal involvement and awareness in all Environmental, Health and      Safety matters</li>\r\n<li>Upkeep Daily Production Record.</li>\r\n</ul>\r\n<p>&nbsp;<strong>Requirements:</strong></p>\r\n<ul>\r\n<li>Possess&nbsp;minimum      Certificate/Diploma in Electrical / Mechanical.</li>\r\n<li>Applicants must be willing to      work in Klang.</li>\r\n<li>Willing to work in shift      rotation</li>\r\n<li>Fresh graduate are encouraged      to apply</li>\r\n</ul>', 115, 5, '800', 1, 2, 0, '2012-08-08 06:21:23', '2012-08-08 07:18:41', NULL, '2012-09-07 15:18:41', 37, 2),
