(144, 'COMM / LOGISTIC CLERK', '<ul>\r\n<li>SPM / CERTIFICATE</li>\r\n<li>1 YEAR WORKING EXPERIENCE</li>\r\n</ul>\r\n<p>Job Description :</p>\r\n<ul>\r\n<li>Arrangements of delivery schedules with transportation to ensure delivery on time</li>\r\n<li>Preparing delivering schedules with transportation to ensure delivery are on time and assisting the Commercial Executive in executing the invoices &amp; export shipping documentation.</li>\r\n<li>Correspondence &amp; liaison with the customers and the transporter</li>\r\n<li>Prepares Delivery Order for the customers&nbsp;</li>\r\n<li>Any other responsibility which may be assigned to you from time to time</li>\r\n</ul>', 338, 4, '-', 1, 1, 0, '2012-11-08 08:26:09', '2012-11-08 08:38:02', NULL, '2012-12-08 16:38:02', 37, 2),
(145, 'MARKETING EXECUTIVE', '<ul>\r\n<li>DIPLOMA / CERTIFICATE / SPM</li>\r\n<li>HAVE SKILLS IN COMMUNICATION</li>\r\n<li>JOB DESCRIPTION : To Offer Personal Loan To Government Servant and GLC.</li>\r\n<li>Based on commission&nbsp;</li>\r\n<li>Posess own transport</li>\r\n<li>Training Provided</li>\r\n<li>Experience : Yes / No</li>\r\n</ul>', 339, 4, '-', 1, 1, 0, '2012-11-08 09:01:45', '2012-11-08 09:10:21', NULL, '2012-12-08 17:10:21', 1, 2),
